import { Route, Routes, Navigate } from "react-router-dom";
import Signup from "./components/Signup";
import Login from "./components/Login";
import EmailVerify from "./components/EmailVerify";
import AdminHomePage from "./components/Main/index"; // Correct import path
import UserHomePage from "./components/userMain/"; // Correct import path

function App() {
  const token = localStorage.getItem("token");
  const role = localStorage.getItem("role");

  return (
    <Routes>
      {/* Role-based redirection for authenticated users */}
      {token && role === "admin" && <Route path="/" exact element={<AdminHomePage />} />}
      {token && role === "user" && <Route path="/" exact element={<UserHomePage />} />}
      
      {/* Public routes */}
      <Route path="/signup" exact element={<Signup />} />
      <Route path="/login" exact element={<Login />} />
      <Route path="/users/:id/verify/:token" element={<EmailVerify />} />

      {/* Redirect unauthenticated users to login */}
      <Route path="/" element={<Navigate replace to="/login" />} />
    </Routes>
  );
}

export default App;
