import { Route, Routes, Navigate } from "react-router-dom";
import Main from "./components/Main";
import Signup from "./components/Singup";
import Login from "./components/Login";
import EmailVerify from "./components/EmailVerify";
import AdminHomePage from "./components/Main"; // Adjust path as needed
import UserHomePage from "./components/userMain"; // Adjust path as needed

function App() {
  const user = localStorage.getItem("token");

  return (
    <Routes>
      {/* Route for authenticated users */}
      {user && <Route path="/" exact element={<Main />} />}
      
      {/* Public routes */}
      <Route path="/signup" exact element={<Signup />} />
      <Route path="/login" exact element={<Login />} />
      <Route path="/users/:id/verify/:token" element={<EmailVerify />} />

      {/* Admin and User home routes */}
      <Route path="/admin-home" exact element={<AdminHomePage />} />
      <Route path="/user-home" exact element={<UserHomePage />} />

      {/* Redirect unauthenticated users to login */}
      <Route path="/" element={<Navigate replace to="/login" />} />
    </Routes>
  );
}

export default App;
